import React from 'react';
import Header from './Header';
import Footer from './Footer';
import Note from './Note';
import CreateArea from './CreateArea';



function App(){
  const [notes, setNote] = useState([]);

  function addNote(newNote){
    setNotes(prevNote => {
      [...prevNote,newNote]
    })
  }

return (
  <div>
   <Header />
    <CreateArea
      onAdd={addNote}
      />
        <Note
          key={1}
          title={"note title"}
          content={"note content"}
          />
   <Footer />
  </div>
  );

}
export default App;
